import React from 'react';
import PropTypes from 'prop-types';

// App 컴포넌트
function App() {
  return (
    <div>
      {foodILike.map(function(dish) {return <Food key={dish.id} name={dish.name} picture={dish.image} rating={dish.rating} />})}
    </div>
  )
}



// 데이터 형성
const foodILike = [
  {
    id : 1,
    name : 'mandu',
    image : 'http://aeriskitchen.com/wp-content/uploads/2020/09/Rolling_Dumplings_01-.jpg',
    rating : 5.0
  },
  {
    id : 2,
    name : 'tteokbokki',
    image : 'http://aeriskitchen.com/wp-content/uploads/2020/06/Seafood_Tteokbokki_01-1-1.jpg',
    rating : 4.9
  },
  {
    id : 3,
    name : 'jjajangmyeon',
    image : 'http://aeriskitchen.com/wp-content/uploads/2008/09/black_bean_paste-_myeon_00-.jpg',
    rating : 4.8
  }
];

// Food 컴포넌트
function Food(props) {
  return (
    <div>
      <h1>I Like {props.name}</h1>
      <h4>{props.rating} / 5.0</h4>
      <img src={props.picture} alt={props.name} />
    </div>
  )
}

// prop-types
Food.PropTypes = {
  name : PropTypes.string.isRequired,
  picture : PropTypes.string.isRequired,
  rating : PropTypes.number.isRequired,
};

export default App;
