import React from 'react';

class App extends React.Component {
  state = {
    count : 0
  };

  render() {
    return (
      <div>
        <h1>The Number is {this.state.count}</h1>
        <button onClick={this.add}>Add</button>
        <button onClick={this.minus}>Minus</button>
      </div>
    )
  }

  add() {
    this.setState({count:1})
  }

  minus() {
    this.setState({count:-1})
  }

}

export default App;
