import React from 'react';

// App 컴포넌트
function App() {
  console.log(foodILike.map(renderFood))
  return (
    <div>
      {foodILike.map(renderFood)}
    </div>
  )
}

  // map 함수 인자로 전달
  function renderFood(dish) {
    return <Food name={dish.name} picture={dish.image} />
  }



// 데이터 형성
const foodILike = [
  {
    name : 'mandu',
    image : 'http://aeriskitchen.com/wp-content/uploads/2020/09/Rolling_Dumplings_01-.jpg'
  },
  {
    name : 'tteokbokki',
    image : 'http://aeriskitchen.com/wp-content/uploads/2020/06/Seafood_Tteokbokki_01-1-1.jpg'
  },
  {
    name : 'jjajangmyeon',
    image : 'http://aeriskitchen.com/wp-content/uploads/2008/09/black_bean_paste-_myeon_00-.jpg'
  }
]

// Food 컴포넌트
function Food(props) {
  return (
    <div>
      <h1>I Like {props.name}</h1>
      <img src={props.picture} />
    </div>
  )
}

export default App;
